:host {
  display: block;
  overflow-x: hidden;

  .intro {
    margin: 3.5rem 2rem;
  }

  .metrics {
    opacity: 0;

    &.visible {
      animation: fadeIn 2s ease-in forwards;
    }
  }

  .in-depth {
    opacity: 0;

    &.visible {
      overflow: hidden;
      animation: slideIn 2s ease-in-out forwards;
    }
  }
}

@keyframes fadeIn {
  0% {
    opacity: 0;
  }

  100% {
    opacity: 1;
  }
}

@keyframes slideIn {
  0% {
    transform: translateX(200%);
    opacity: 1;
  }

  100% {
    transform: translateX(0);
    opacity: 1;
  }
}

