@use 'utils';
@use 'variables' as vars;

:host {
  display: block;

  .sr-only {
    @include utils.sr-only();
  }

  .table th {
    background-color: #fff7f8;
    color: #59264d;
    text-align: right;

    border-top: 0.063rem solid #d3d3d3;
    border-bottom: 0.063rem solid #d3d3d3;
    border-left: 0.063rem solid #d3d3d3;
  }

  .table td {
    border-bottom: 0.063rem solid #d3d3d3;
    border-left: 0.063rem solid #d3d3d3;
  }

  .table tr:first-child td {
    border-top: 0.063rem solid #d3d3d3;
  }

  tr > td:first-child {
    text-align: left;
    padding: 0 2rem;
  }

  tr > th:first-child {
    text-align: left;
    padding: 0 2rem;
  }

  tr > td:last-of-type {
    border-right: 0.063rem solid #d3d3d3;
  }

  tr > th:last-of-type {
    border-right: 0.063rem solid #d3d3d3;
  }

  .type {
    height: 3rem;
    max-width: 26rem;
    min-width: 17rem;
    border-radius: 0rem;
    white-space: nowrap;
  }

  .count {
    text-align: right;
    height: 3rem;
    min-width: 6.5rem;
  }

  .icon-header {
    max-width: 5rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  .icon-cell {
    max-width: 5rem;
    min-height: 3rem;

    .icon {
      margin: auto;
    }
  }
}

::ng-deep .biomarker-table-hover.full-column-name {
  display: flex;
  padding: 1rem 1.5rem;
  border-radius: 0.5rem;
  box-shadow: 0.063rem 0.125rem 1rem 0.125rem rgba(0, 0, 0, 0.16);
  background-color: vars.$primary-contrast-rgb;
}
