import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, input } from '@angular/core';
import { MatIconModule } from '@angular/material/icon';
import { RouterModule } from '@angular/router';
import { ButtonModule } from '@hra-ui/design-system/button';

/** Breadcrumb item */
interface BreadcrumbItem {
  /** Name of item */
  name: string;
  /** Route to page */
  route?: string;
}

/**
 * Component used to help the user understand their location within websites
 */
@Component({
  selector: 'hra-breadcrumbs',
  standalone: true,
  imports: [CommonModule, MatIconModule, ButtonModule, RouterModule],
  templateUrl: './breadcrumbs.component.html',
  styleUrl: './breadcrumbs.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class BreadcrumbsComponent {
  /** Crumbs to display */
  readonly crumbs = input<BreadcrumbItem[]>([]);
}

import { computed, Directive, input } from '@angular/core';

/** Input options for icon button size */
export type IconButtonSize = 'small' | 'medium' | 'large';

/** Interface for ButtonToggle Config */
interface ButtonToggleConfig {
  /** Line height of the button */
  lineHeight: number;
  /** Font variable of the button toggle */
  font: string;
}

/** Record of button sizes (number in rem) */
const BUTTON_CONFIG: Record<IconButtonSize, ButtonToggleConfig> = {
  small: {
    lineHeight: 18,
    font: '--sys-label-small',
  },
  medium: {
    lineHeight: 21,
    font: '--sys-label-medium',
  },
  large: {
    lineHeight: 24,
    font: '--sys-label-large',
  },
};

/**
 * Directive for icon buttons
 */
@Directive({
  selector: '[hraButtonToggleSize]',
  standalone: true,
  host: {
    '[style.--mat-standard-button-toggle-height]': 'lineHeight()',
    '[style.--mat-standard-button-toggle-label-text-line-height]': 'lineHeight()',
    '[style.font]': 'fontVar()',
  },
})
export class ToggleButtonSizeDirective {
  /** Size of icon button to use */
  readonly size = input.required<IconButtonSize>({ alias: 'hraButtonToggleSize' });

  /** Gets the font variable for the current button size */
  protected readonly fontVar = computed(() => `var(${BUTTON_CONFIG[this.size()].font})`);

  /** Gets the line height for the current button size */
  protected readonly lineHeight = computed(() => `${BUTTON_CONFIG[this.size()].lineHeight}px`);
}

import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, input } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatSidenavModule } from '@angular/material/sidenav';
import { AssetUrlPipe } from '@hra-ui/cdk/app-href';
import { AppsCardComponent } from '@hra-ui/design-system/apps-card';
import { IconButtonSizeDirective } from '@hra-ui/design-system/icon-button';
import { NavHeaderButtonsComponent, NavHeaderButtonsVariant } from '@hra-ui/design-system/nav-header-buttons';
import { ScrollingModule } from '@hra-ui/design-system/scrolling';
import { SoftwareStatus } from '@hra-ui/design-system/software-status-indicator';

/** Sidenav card data */
export interface CardData {
  /** Name of card category */
  category: string;
  /** Info for apps in category */
  cards: {
    /** Name of app */
    name: string;
    /** Icon path */
    icon: string;
    /** App title */
    title: string;
    /** App description */
    description: string;
    /** Link to app */
    link: string;
  }[];
}

/**
 * Navigation Header Component
 */
@Component({
  selector: 'hra-nav-header',
  standalone: true,
  imports: [
    CommonModule,
    MatButtonModule,
    MatIconModule,
    NavHeaderButtonsComponent,
    IconButtonSizeDirective,
    MatSidenavModule,
    AppsCardComponent,
    ScrollingModule,
    AssetUrlPipe,
  ],
  templateUrl: './nav-header.component.html',
  styleUrl: './nav-header.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class NavHeaderComponent {
  /** Variant of the header */
  readonly variant = input<NavHeaderButtonsVariant>('basic');
  /** Link to the app */
  readonly link = input.required<string>();
  /** Current app */
  readonly app = input.required<string>();
  /** Name of the app */
  readonly title = input.required<string>();
  /** Status of the app */
  readonly status = input<SoftwareStatus>();
  /** Data to display in sidenav */
  readonly navigationCategories = input.required<CardData[]>();
}

import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, input, output } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatTableModule } from '@angular/material/table';

/** An item which defines a string label and a string value */
export interface DataItem {
  /** A string property that represents the label */
  label: string;
  /** A string property that represents the value */
  value: string;
}

/** Info modal variants */
export type InfoModalVariant = 'tabular' | 'center';

/**
 * Modal to display all available information about specific areas of a dataset.
 */
@Component({
  selector: 'hra-info-modal',
  standalone: true,
  imports: [CommonModule, MatTableModule, MatIconModule, MatButtonModule],
  templateUrl: './info-modal.component.html',
  styleUrl: './info-modal.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
  host: {
    '[class.center]': 'variant() == "center"',
  },
})
export class InfoModalComponent {
  /** List of data items to display */
  data = input<DataItem[]>([]);

  /** Variant of info modal to use */
  variant = input<InfoModalVariant>('tabular');

  /** Title of modal */
  title = input.required<string>();

  /** Emits when close icon clicked */
  close = output<void>();
}

import { HttpFeature, HttpFeatureKind, provideHttpClient } from '@angular/common/http';
import { EnvironmentProviders, makeEnvironmentProviders } from '@angular/core';
import { provideAnimations } from '@angular/platform-browser/animations';
import { provideIcons } from '@hra-ui/cdk/icons';
import { provideButtons } from '@hra-ui/design-system/button';
import { provideButtonToggle } from '@hra-ui/design-system/button-toggle';
import { provideIconButtons } from '@hra-ui/design-system/icon-button';
import { provideInput } from '@hra-ui/design-system/input';
import { provideMenu } from '@hra-ui/design-system/menu';
import { provideScrolling, ScrollingOptions } from '@hra-ui/design-system/scrolling';
import { provideSelect } from '@hra-ui/design-system/select';
import { provideTable } from '@hra-ui/design-system/table';
import { provideTrees } from '@hra-ui/design-system/tree';

/** Design system provider options */
export interface DesignSystemOptions {
  /** Http features */
  http?: HttpFeature<HttpFeatureKind>[];
  /** Scrolling options */
  scrolling?: ScrollingOptions;
}

/** Get the providers shared between prod and testing */
export function provideDesignSystemCommon(options?: DesignSystemOptions) {
  return [
    provideIcons({
      fontIcons: {
        defaultClasses: ['material-symbols-rounded'],
      },
    }),
    provideButtons(),
    provideIconButtons(),
    provideTrees(),
    provideScrolling(options?.scrolling),
    provideMenu(),
    provideTable(),
    provideSelect(),
    provideInput(),
    provideButtonToggle(),
  ];
}

/**
 * Returns design system providers
 */
export function provideDesignSystem(options?: DesignSystemOptions): EnvironmentProviders {
  return makeEnvironmentProviders([
    provideHttpClient(...(options?.http ?? [])),
    provideAnimations(),
    ...provideDesignSystemCommon(options),
  ]);
}

import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, inject } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import {
  MAT_DIALOG_DATA,
  MatDialogActions,
  MatDialogClose,
  MatDialogContent,
