import { ArrayDataSource } from '@angular/cdk/collections';
import { AfterViewInit, ChangeDetectionStrategy, Component, computed, input, ViewChild } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatTree, MatTreeModule } from '@angular/material/tree';

import { TreeSize, TreeSizeDirective } from '../tree-size/tree-size.directive';

/** Nested tree node data */
export interface NestedNode {
  /** Name of node */
  name: string;
  /** List of child nodes */
  children?: NestedNode[];
}

/** Padding indents for each tree size (px) */
const PADDING: Record<TreeSize, number> = {
  small: 32,
  medium: 36,
  large: 40,
};

/**
 * Angular Material nested tree component with HRA styles
 */
@Component({
  selector: 'hra-tree-demo',
  templateUrl: 'tree-demo.component.html',
  standalone: true,
  imports: [MatTreeModule, MatButtonModule, MatIconModule, TreeSizeDirective],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TreeDemoComponent implements AfterViewInit {
  /** ViewChild for tree component */
  @ViewChild(MatTree) tree!: MatTree<NestedNode>;

  /** Tree node data */
  readonly treeData = input<NestedNode[]>([]);

  /** Size of tree to use */
  readonly size = input<TreeSize>('medium');

  /** Padding indents */
  protected readonly padding = computed(() => PADDING[this.size()]);

  /** Data source */
  dataSource = new ArrayDataSource<NestedNode>([]);

  /** Current selected node */
  selectedNode?: NestedNode;

  /** Gets the children of a node */
  childrenAccessor = (dataNode: NestedNode) => dataNode.children ?? [];

  /** If the node has a child */
  hasChild = (_: number, node: NestedNode) => !!node.children?.length;

  /** Sets dataSource data after view init */
  ngAfterViewInit() {
    this.dataSource = new ArrayDataSource<NestedNode>(this.treeData());
  }

  /** Renders node if it is a root node or if all of its ancestors are expanded */
  shouldRender(node: NestedNode): boolean {
    const parent = this.getParentNode(node);
    return !parent || (!!this.tree?.isExpanded(parent) && this.shouldRender(parent));
  }

  /** Gets parent of a node */
  private getParentNode(node: NestedNode): NestedNode | undefined {
    for (const parent of this.treeData()) {
      if (parent.children?.includes(node)) {
        return parent;
      }
    }
    return undefined;
  }
}

import type { ScrollTimelineFunc } from '@hra-ui/design-system/scrolling';

/** Globals used by scrolling */
interface ScrollingGlobals {
  /** Scroll timeline constructor */
  ScrollTimeline?: ScrollTimelineFunc;
}

/**
 * Setup the testing environment for scrolling to work.
 * Adds global mocks for functions used by scrolling if they don't exist.
 */
export function setupScrollTesting(): void {
  const globals = globalThis as ScrollingGlobals;
  if (typeof globals.ScrollTimeline !== 'function') {
    globals.ScrollTimeline = jest.fn();
  }

  if (typeof HTMLElement.prototype.animate !== 'function') {
    HTMLElement.prototype.animate = jest.fn(
      () =>
        ({
          cancel: jest.fn(),
        }) as unknown as Animation,
    );
  }
}

import { EnvironmentProviders, NgModule, makeEnvironmentProviders } from '@angular/core';
import { getCurrentScriptBasePath } from '@hra-ui/cdk/app-href';
import { provideStyleComponents } from '@hra-ui/cdk/styling';
import {
  NgScrollbarModule,
  NgScrollbarOptions,
  provideScrollbarOptions,
  provideScrollbarPolyfill,
} from 'ngx-scrollbar';
import { ScrollOverflowFadeDirective } from './scroll-overflow-fade/scroll-overflow-fade.directive';
import { ScrollbarStylesComponent } from './scrollbar-styles/scrollbar-styles.component';

/** Scrolling configuration */
export interface ScrollingOptions extends NgScrollbarOptions {
  /** Url to the scroll timeline polyfill script */
  polyfillUrl?: string;
}

/** Default scroll timeline polyfill url */
const DEFAULT_POLYFILL_URL = 'assets/polyfills/scroll-timeline-polyfill.js';

/**
 * Provide scrolling functionality to an application.
 *
 * @param options Scrollbar options
 * @returns An environment provider
 */
export function provideScrolling(options?: ScrollingOptions): EnvironmentProviders {
  const polyfillUrl = getCurrentScriptBasePath() + (options?.polyfillUrl ?? DEFAULT_POLYFILL_URL);

  return makeEnvironmentProviders([
    provideStyleComponents(ScrollbarStylesComponent),
    provideScrollbarPolyfill(polyfillUrl),
    provideScrollbarOptions({
      visibility: 'hover',
      appearance: 'compact',
      trackClass: 'hra-scrollbar-track',
      thumbClass: 'hra-scrollbar-thumb',
      ...options,
    }),
  ]);
}

/** Module exporting ng-scrollbar and related scrolling utilities */
@NgModule({
  imports: [ScrollOverflowFadeDirective],
  exports: [NgScrollbarModule, ScrollOverflowFadeDirective],
})
export class ScrollingModule {}

import { CommonModule } from '@angular/common';
import { ChangeDetectionStrategy, Component, inject } from '@angular/core';
import { MatIconModule } from '@angular/material/icon';
import { MAT_SNACK_BAR_DATA, MatSnackBarRef } from '@angular/material/snack-bar';
import { ButtonModule } from '@hra-ui/design-system/button';
import { IconButtonSizeDirective } from '@hra-ui/design-system/icon-button';

/** Type for button position */
