/** Snackbar component */
@Component({
  selector: 'hra-snackbar',
  standalone: true,
  imports: [CommonModule, MatIconModule, ButtonModule, IconButtonSizeDirective, MatIconModule],
  templateUrl: './snackbar.component.html',
  styleUrl: './snackbar.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class SnackbarComponent {
  /** Reference to the MatSnackbarRef */
  protected readonly snackbarRef = inject(MatSnackBarRef);

  /** Injection token for the snackbar data*/
  protected readonly data: SnackbarData = inject(MAT_SNACK_BAR_DATA);
}

import { CommonModule } from '@angular/common';
import { Component, effect, input, viewChild } from '@angular/core';
import { MatSort, MatSortModule } from '@angular/material/sort';
import { MatTableDataSource, MatTableModule } from '@angular/material/table';

/** Type for row data */
export interface TableDemoData {
  /** Name of item */
  name: string;
  /** Value of item*/
  value: number;
}

/**
 * Angular Material table with with sort feature
 */
@Component({
  selector: 'hra-table-demo',
  templateUrl: 'table-demo.component.html',
  standalone: true,
  imports: [MatTableModule, MatSortModule, CommonModule],
})
export class TableDemoComponent {
  /** Unsorted data */
  readonly data = input<TableDemoData[]>([]);

  /** Columns in table */
  readonly columns: string[] = ['name', 'value'];

  /** Mat sort element */
  readonly sort = viewChild.required(MatSort);

  /** Table data source */
  readonly dataSource = new MatTableDataSource<TableDemoData>([]);

  /** Sort data on load */
  constructor() {
    effect(() => {
      this.dataSource.data = this.data();
      this.dataSource.sort = this.sort();
    });
  }
}

import { CommonModule } from '@angular/common';
import { booleanAttribute, ChangeDetectionStrategy, Component, input } from '@angular/core';

/** Interface for Tooltip Card Content */
export interface TooltipContent {
