import { Directive, inject, output, TemplateRef, ViewContainerRef } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { Subscription } from 'rxjs';
import { FullscreenComponent } from './fullscreen/fullscreen.component';

/** Fullscreen directive */
@Directive({
  selector: '[hraFullscreen]',
  standalone: true,
  exportAs: 'hraFullscreen',
})
export class FullscreenDirective {
  /** Output to emit when fullscreen is closed */
  readonly closed = output<void>({ alias: 'hraFullscreenClosed' });

  /** Returns the native element of the view reference */
  get nativeElement(): HTMLElement {
    return this.viewRef.rootNodes[0];
  }

  /** Reference to the mat dialog */
  private readonly dialog = inject(MatDialog);
  /** Reference to the template */
  private readonly templateRef = inject(TemplateRef);
  /** Reference to the view container */
  private readonly viewContainerRef = inject(ViewContainerRef);
  /** Create view reference using the template */
  private readonly viewRef = this.viewContainerRef.createEmbeddedView(this.templateRef);

  /** Detaches the view from histogram module and attaches it to the view in the dialog */
  open(): void {
    const { viewContainerRef, viewRef } = this;
    const index = viewContainerRef.indexOf(viewRef);
    viewContainerRef.detach(index);

    const dialogRef = this.dialog.open(FullscreenComponent, {
      data: viewRef,
    });

    const subs = new Subscription();
    const sub1 = dialogRef.beforeClosed().subscribe(() => {
      viewContainerRef.insert(viewRef);
    });

    const sub2 = dialogRef.afterClosed().subscribe(() => {
      this.closed.emit();
    });

    subs.add(sub1);
    subs.add(sub2);
  }
}

import { computed, Directive, input } from '@angular/core';

/** Input options for icon button size */
export type IconButtonSize = 'small' | 'large';

/** Record of button sizes (number in rem) */
const BUTTON_SIZES: Record<IconButtonSize, number> = {
  small: 1.5,
  large: 2.5,
};

/** Record of icon sizes (number in rem) */
const ICON_SIZES: Record<IconButtonSize, number> = {
  small: 1.25,
  large: 1.5,
};

/**
 * Directive for icon buttons
 */
@Directive({
  selector: '[hraIconButtonSize]',
  standalone: true,
  host: {
    '[style.--mdc-icon-button-state-layer-size.rem]': 'buttonSize()',
    '[style.--mdc-icon-button-icon-size.rem]': 'iconSize()',
  },
})
export class IconButtonSizeDirective {
  /** Size of icon button to use */
  readonly size = input.required<IconButtonSize>({ alias: 'hraIconButtonSize' });

  /** Gets size of button in rem */
  protected readonly buttonSize = computed(() => BUTTON_SIZES[this.size()]);
  /** Gets size of icon in rem */
  protected readonly iconSize = computed(() => ICON_SIZES[this.size()]);
}

import { Directive, effect, inject, input } from '@angular/core';
import { MatTooltip } from '@angular/material/tooltip';
import { registerStyleComponents } from '@hra-ui/cdk/styling';
import { MicroTooltipStylesComponent } from './micro-tooltip-styles/micro-tooltip-styles.component';

/** Type of Tooltip size */
export type MicroTooltipSize = 'small' | 'medium';

/** Directive for Tooltip */
@Directive({
  selector: '[hraMicroTooltip]',
  standalone: true,
  hostDirectives: [
    {
      directive: MatTooltip,
      inputs: ['matTooltip: hraMicroTooltip', 'matTooltipPosition: hraMicroTooltipPosition'],
    },
  ],
})
export class MicroTooltipDirective {
  /** Size of the tooltip */
  readonly size = input<MicroTooltipSize>('medium', { alias: 'hraMicroTooltipSize' });

  /** Instance of MatTooltip */
  protected readonly tooltip = inject(MatTooltip);

  /** Registers the styles and sets class names for the tooltip container */
  constructor() {
    registerStyleComponents([MicroTooltipStylesComponent]);

    effect(() => {
      this.tooltip.tooltipClass = ['hra-micro-tooltip', `hra-micro-tooltip-${this.size()}`];
    });
  }
}

import { Directive } from '@angular/core';

/** Directive for navigation category button */
@Directive({
  selector: '[hraNavCatButton]',
