mat-tree {
  --mat-tree-container-background-color: var(--sys-on-primary);
}

mat-tree-node {
  --mat-tree-node-text-font: var(--sys-label-medium-font);
  --mat-tree-node-text-weight: var(--sys-label-medium-weight);
  --mat-tree-node-text-color: var(--sys-secondary);

  letter-spacing: var(--sys-label-medium-tracking);
  cursor: pointer;
  user-select: none;
  outline: none;

  &:focus-visible .node-content {
    border-color: var(--sys-tertiary);
  }
}

.node-content {
  button {
    --mat-icon-button-hover-state-layer-opacity: 0;
    --mat-icon-button-pressed-state-layer-opacity: 0;
  }

  display: flex;
  height: var(--mat-tree-node-min-height);
  line-height: var(--mat-tree-node-min-height);
  border-radius: 0.25rem;
  width: 100%;
  border-width: 2px;
  border-style: solid;
  border-color: transparent;

  &.leaf {
    padding-left: 0.5rem;
  }

  &:hover {
    background: rgb(from var(--sys-secondary) r g b / 0.08);
  }

  &:active {
    background: rgb(from var(--sys-secondary) r g b / 0.16);
    border-color: transparent;
  }

  &.selected {
    background: rgb(from var(--sys-tertiary) r g b / 0.2);
  }
}

